package com.hps.integrator.tests.applepay;

import com.hps.integrator.applepay.ecv1.PaymentData;
import com.hps.integrator.applepay.ecv1.PaymentToken;
import com.hps.integrator.infrastructure.HpsException;
import com.hps.integrator.tests.testdata.TestData;
import org.junit.Rule;
import org.junit.Test;
import org.junit.rules.ExpectedException;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertNotNull;

public class PaymentTokenTests
{
    @Rule
    public ExpectedException expectedEx = ExpectedException.none();

    @Test
    public void empty_json_throws_exception() throws HpsException
    {
        expectedEx.expect(HpsException.class);
        expectedEx.expectMessage(PaymentData.EMPTY_OR_NULL_JSON_STRING);
        new PaymentToken("");
    }

    @Test
    public void null_json_throws_exception() throws HpsException
    {
        expectedEx.expect(HpsException.class);
        expectedEx.expectMessage(PaymentData.EMPTY_OR_NULL_JSON_STRING);
        new PaymentToken(null);
    }

    @Test
    public void can_parse_token_json() throws HpsException
    {
        PaymentToken token = new PaymentToken(TestData.PAYMENT_TOKEN_MC_3DS_JSON);
        assertNotNull(token);
        assertEquals(token.getData(), "");
        assertEquals(token.getSignature(), "");
        assertEquals(token.getHeader().getEphemeralPublicKey(), "XXXXXXXXXXZIzj0CAQYIKoZIzj0DAQcDQgAElJ0FU66tKNvnBqvkykrTsVw37gw1kRW2jLd4N4jqvqGuHB+KaiWcrMKEI5SI0AKTgR6zIEnYahZmOd4ScZHqOQ==");
        assertEquals(token.getHeader().getPublicKeyHash(), "XXXXXXXXXX/t54x445nFoX8BcHVeenIHikQRURYqjuY=");
        assertEquals(token.getHeader().getTransactionId(), "XXXXXXXXXX561c4b4c1c5d2c4de38b124f01a67bbe3ab8910b91a8991c293c71");
        assertEquals(token.getVersion(), "EC_v1");
    }
}